// src/components/SignIn.js
import { NavLink, useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [forgotPasswordEmail, setForgotPasswordEmail] = useState('');
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const [registerEmail, setRegisterEmail] = useState('');
  const [registerPassword, setRegisterPassword] = useState('');
  const navigate = useNavigate();
  
  // Check if user is already logged in
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      navigate('/'); // Redirect to home if already logged in
    }
  }, [navigate]);
  
  // API URL - adjust this to point to your PHP file
  const API_URL = 'http://localhost/frontendREACT/auth.php';
  
  async function handleSubmit(e) {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage('');
    setSuccessMessage('');
    
    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'login',
          username: email,
          password: password
        }),
      });
      
      const data = await response.json();
      
      if (data.success) {
        setSuccessMessage(data.message);
        // Store user info in localStorage or context for session management
        localStorage.setItem('user', JSON.stringify(data.user));
        
        // Short delay to show success message before redirecting
        setTimeout(() => {
          navigate('/'); // Redirect to home page
        }, 1500);
      } else {
        setErrorMessage(data.message);
      }
    } catch (error) {
      setErrorMessage('Error connecting to server. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  }
  
  const handleCreateAccount = async (e) => {
    e.preventDefault();
    
    if (!registerEmail || !registerPassword) {
      setErrorMessage('Email and password are required');
      return;
    }
    
    setIsLoading(true);
    setErrorMessage('');
    setSuccessMessage('');
    
    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'register',
          username: registerEmail,
          password: registerPassword
        }),
      });
      
      const data = await response.json();
      
      if (data.success) {
        setSuccessMessage(data.message);
        setShowRegisterForm(false);
        // Reset form fields
        setRegisterEmail('');
        setRegisterPassword('');
        
        // Automatically log in after successful registration
        setTimeout(() => {
          setEmail(registerEmail);
          setPassword(registerPassword);
          // Simulate a click on the login button
          handleSubmit(new Event('submit'));
        }, 1500);
      } else {
        setErrorMessage(data.message);
      }
    } catch (error) {
      setErrorMessage('Error connecting to server. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleForgotPassword = (e) => {
    e.preventDefault();
    
    if (!forgotPasswordEmail) {
      setErrorMessage('Please enter your email address');
      return;
    }
    
    // In a real app, you would send a password reset request to the server
    setSuccessMessage('Password reset link sent to your email');
    setShowForgotPassword(false);
    setForgotPasswordEmail('');
  };
  
  const toggleRegisterForm = () => {
    setShowRegisterForm(!showRegisterForm);
    setErrorMessage('');
    setSuccessMessage('');
  };

  const toggleForgotPassword = () => {
    setShowForgotPassword(!showForgotPassword);
    setErrorMessage('');
    setSuccessMessage('');
  };
  
  return (
    <section className="signin-container">
      <h2>Welcome Back</h2>
      <p>Sign in to access your personalized beauty recommendations</p>
      
      {errorMessage && (
        <div className="error-message">
          {errorMessage}
        </div>
      )}
      
      {successMessage && (
        <div className="success-message">
          {successMessage}
        </div>
      )}
      
      {!showRegisterForm && !showForgotPassword && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email/Username</label>
            <input 
              type="text" 
              id="email" 
              className="form-control" 
              value={email}
              onChange={e => setEmail(e.target.value)} 
              required 
              placeholder="your@email.com"
              disabled={isLoading}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input 
              type="password" 
              id="password" 
              className="form-control" 
              value={password}
              onChange={e => setPassword(e.target.value)} 
              required
              placeholder="••••••••"
              disabled={isLoading}
            />
            <div className="forgot-password">
              <a href="#" onClick={toggleForgotPassword}>
                Forgot password?
              </a>
            </div>
          </div>
          
          <button 
            type="submit" 
            className="signin-btn"
            disabled={isLoading}
          >
            {isLoading ? 'Signing in...' : 'Sign In'}
          </button>
        </form>
      )}

      {showForgotPassword && (
        <form onSubmit={handleForgotPassword}>
          <div className="form-group">
            <label htmlFor="forgotEmail">Email Address</label>
            <input 
              type="email" 
              id="forgotEmail" 
              className="form-control" 
              value={forgotPasswordEmail}
              onChange={e => setForgotPasswordEmail(e.target.value)} 
              required 
              placeholder="your@email.com"
              disabled={isLoading}
            />
          </div>
          
          <button 
            type="submit" 
            className="signin-btn"
            disabled={isLoading}
          >
            Send Reset Link
          </button>
          
          <button 
            type="button" 
            className="secondary-btn"
            onClick={toggleForgotPassword}
            disabled={isLoading}
          >
            Back to Sign In
          </button>
        </form>
      )}
      
      {showRegisterForm && (
        <form onSubmit={handleCreateAccount}>
          <div className="form-group">
            <label htmlFor="registerEmail">Email/Username</label>
            <input 
              type="text" 
              id="registerEmail" 
              className="form-control" 
              value={registerEmail}
              onChange={e => setRegisterEmail(e.target.value)} 
              required 
              placeholder="your@email.com"
              disabled={isLoading}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="registerPassword">Password</label>
            <input 
              type="password" 
              id="registerPassword" 
              className="form-control" 
              value={registerPassword}
              onChange={e => setRegisterPassword(e.target.value)} 
              required
              placeholder="••••••••"
              disabled={isLoading}
            />
          </div>
          
          <button 
            type="submit" 
            className="signin-btn"
            disabled={isLoading}
          >
            {isLoading ? 'Creating Account...' : 'Create Account'}
          </button>
          
          <button 
            type="button" 
            className="secondary-btn"
            onClick={toggleRegisterForm}
            disabled={isLoading}
          >
            Back to Sign In
          </button>
        </form>
      )}
      
      {!showRegisterForm && !showForgotPassword && (
        <>
          <div className="or-divider">
            <span>or</span>
          </div>
          
          <div className="social-signin">
            <button className="social-btn google">
              <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 18 18">
                <path fill="#4285F4" d="M17.64 9.2c0-.637-.057-1.251-.164-1.84H9v3.481h4.844c-.209 1.125-.843 2.078-1.796 2.716v2.259h2.908c1.702-1.567 2.684-3.875 2.684-6.615z"/>
                <path fill="#34A853" d="M9 18c2.43 0 4.467-.806 5.956-2.18l-2.908-2.259c-.806.54-1.837.86-3.048.86-2.344 0-4.328-1.584-5.036-3.711H.957v2.332A8.997 8.997 0 0 0 9 18z"/>
                <path fill="#FBBC05" d="M3.964 10.71A5.41 5.41 0 0 1 3.682 9c0-.593.102-1.17.282-1.71V4.958H.957A8.996 8.996 0 0 0 0 9c0 1.452.348 2.827.957 4.042l3.007-2.332z"/>
                <path fill="#EA4335" d="M9 3.58c1.321 0 2.508.454 3.44 1.345l2.582-2.58C13.463.891 11.426 0 9 0A8.997 8.997 0 0 0 .957 4.958L3.964 7.29C4.672 5.163 6.656 3.58 9 3.58z"/>
              </svg>
              Sign in with Google
            </button>
          </div>
          
          <div className="signup-option">
            <p>Don't have an account?</p>
            <button 
              className="create-account-btn" 
              onClick={toggleRegisterForm}
              disabled={isLoading}
            >
              Create Account
            </button>
          </div>
          
          <div className="skip-option">
            <NavLink to="/">Continue without an account</NavLink>
          </div>
        </>
      )}
    </section>
  );
}
