import { useState, useEffect, useRef } from 'react';
import { Html5QrcodeScanner } from 'html5-qrcode';

export default function Ingredients() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [chatMessage, setChatMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [scannerActive, setScannerActive] = useState(false);
  const [scanResult, setScanResult] = useState('');
  const [scannerStatus, setScannerStatus] = useState('');
  const [scanError, setScanError] = useState('');
  const scannerRef = useRef(null);
  
  useEffect(() => {
    // Clean up scanner on component unmount
    return () => {
      if (scannerRef.current) {
        try {
          scannerRef.current.clear();
        } catch (err) {
          console.error("Failed to clear scanner", err);
        }
      }
    };
  }, []);
  
  useEffect(() => {
    // Initialize scanner when scannerActive is true
    if (scannerActive) {
      setScannerStatus('Starting camera...');
      setScanError('');
      
      // First make sure any existing scanner is cleared
      if (scannerRef.current) {
        try {
          scannerRef.current.clear();
          scannerRef.current = null;
          console.log("Cleared previous scanner");
        } catch (err) {
          console.error("Failed to clear previous scanner", err);
          setScanError("Error clearing previous scanner. Please refresh the page and try again.");
        }
      }

      try {
        // Create a new scanner with better config
        const scanner = new Html5QrcodeScanner("reader", {
          fps: 10,
          qrbox: { width: 250, height: 250 },
          rememberLastUsedCamera: true,
          showTorchButtonIfSupported: true,
          aspectRatio: 1.0,
          formatsToSupport: [
            Html5QrcodeScanner.FORMATSTYPE.EAN_13,
            Html5QrcodeScanner.FORMATSTYPE.EAN_8,
            Html5QrcodeScanner.FORMATSTYPE.CODE_39,
            Html5QrcodeScanner.FORMATSTYPE.CODE_93,
            Html5QrcodeScanner.FORMATSTYPE.CODE_128,
            Html5QrcodeScanner.FORMATSTYPE.UPC_A,
            Html5QrcodeScanner.FORMATSTYPE.UPC_E,
          ]
        });

        // Success callback
        const onScanSuccess = (decodedText, decodedResult) => {
          console.log(`Code scanned: ${decodedText}`, decodedResult);
          setScannerStatus('Success!');
          setScanResult(`Successfully scanned barcode: ${decodedText}`);
          
          // Analyze the scanned barcode
          handleBarcodeAnalysis(decodedText);
          
          // Deactivate scanner after successful scan
          setScannerActive(false);
        };

        // Error callback (just log errors, don't handle them)
        const onScanFailure = (error) => {
          // Don't log frequent scan failures to avoid console spam
          if (error !== "No QR code found.") {
            console.warn(`Code scan error: ${error}`);
            if (error.includes("Camera access denied")) {
              setScanError("Camera access denied. Please allow camera access in your browser settings.");
            } else if (error.includes("No cameras")) {
              setScanError("No cameras found. Please ensure your device has a working camera.");
            }
          }
        };

        // Render scanner with callbacks
        scanner.render(onScanSuccess, onScanFailure);
        
        // Store scanner reference
        scannerRef.current = scanner;
        
        // Update status to reflect camera readiness
        setTimeout(() => {
          if (scannerActive && !scanResult) {
            setScannerStatus('Camera ready! Position barcode in the center of the frame.');
          }
        }, 3000);
        
      } catch (error) {
        console.error("Failed to initialize scanner:", error);
        setScannerStatus('');
        setScanError(`Error initializing scanner: ${error.message}. Please refresh and try again.`);
      }
    } else {
      // Clean up scanner when scannerActive becomes false
      if (scannerRef.current) {
        try {
          scannerRef.current.clear();
          scannerRef.current = null;
          console.log("Scanner deactivated");
        } catch (err) {
          console.error("Failed to clear scanner", err);
        }
      }
      
      // Clear scanner status when not active
      if (!scanResult) {
        setScannerStatus('');
      }
    }
  }, [scannerActive, scanResult]);
  
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setSelectedImage(file);
      
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };
  
  const handleAnalyzeImage = () => {
    if (!selectedImage) return;
    
    setIsAnalyzing(true);
    
    setTimeout(() => {
      setAnalysisResults({
        message: "Analysis complete! In the future, this will identify ingredients from your product label."
      });
      setIsAnalyzing(false);
    }, 2000);
  };
  
  const handleBarcodeAnalysis = (barcode) => {
    setIsAnalyzing(true);
    
    // Add a more visible scan success message immediately
    setScanResult(`Successfully scanned barcode: ${barcode}`);
    
    // Simulate AI processing with the barcode
    setTimeout(() => {
      setAnalysisResults({
        message: `Analysis complete for barcode: ${barcode}. 
        
Product Name: SkinCare Hydrating Moisturizer
        
Main Ingredients: Water, Glycerin, Cetearyl Alcohol, Caprylic/Capric Triglyceride, Ceteareth-20, Butyrospermum Parkii (Shea) Butter, Dimethicone, Phenoxyethanol, Tocopheryl Acetate, Parfum.

Potentially Harmful Ingredients:
• Phenoxyethanol - May cause allergic reactions in sensitive individuals. Risk: Low to Moderate
• Parfum (Fragrance) - Can cause irritation and allergic reactions. Risk: Moderate`
      });
      setIsAnalyzing(false);
      
      // Add this as a message in the chat
      const aiResponse = { 
        sender: 'ai', 
        text: `I found information about product with barcode: ${barcode}.

Product Name: SkinCare Hydrating Moisturizer

This product contains two potentially concerning ingredients:
1. Phenoxyethanol - A preservative that may cause skin irritation in some people
2. Parfum (Fragrance) - Can trigger allergic reactions or sensitivity

Would you like more specific information about these ingredients?` 
      };
      setChatHistory(prev => [...prev, aiResponse]);
    }, 2000);
  };
  
  const handleClearImage = () => {
    setSelectedImage(null);
    setImagePreview(null);
    setAnalysisResults(null);
  };
  
  const toggleScanner = () => {
    setScannerActive(!scannerActive);
    setScanResult('');
    setScanError('');
    if (scannerActive) {
      setAnalysisResults(null);
    }
  };
  
  const testScan = () => {
    // Simulate a successful barcode scan for testing
    const testBarcode = "7940044597";
    setScannerStatus('Success!');
    setScanResult(`Successfully scanned barcode (TEST): ${testBarcode}`);
    handleBarcodeAnalysis(testBarcode);
    setScannerActive(false);
  };
  
  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!chatMessage.trim()) return;
    
    // Add user message to chat history
    const userMessage = { sender: 'user', text: chatMessage };
    setChatHistory(prev => [...prev, userMessage]);
    
    // Simulate AI processing
    setIsProcessing(true);
    
    // Simulate AI response (in a real implementation, this would call Gemini API)
    setTimeout(() => {
      const aiResponse = { 
        sender: 'ai', 
        text: `Regarding your question about "${chatMessage}": In a real implementation, Gemini AI would provide detailed information about these ingredients, their benefits, potential risks, and suitable alternatives.` 
      };
      setChatHistory(prev => [...prev, aiResponse]);
      setChatMessage('');
      setIsProcessing(false);
    }, 1500);
  };
  
  return (
    <section className="ingredients-container">
      <h1>Ingredient Checker</h1>
      <p>Upload a product image to check what's in your beauty products or ask questions about ingredients.</p>
      
      <div className="upload-section">
        <h2>Upload a Product Image</h2>
        <p>Take a photo of your product's ingredient list and we'll analyze it for you.</p>
        
        <div className="upload-container">
          {!imagePreview && !scannerActive ? (
            <div className="upload-options">
              <div className="upload-box">
                <label htmlFor="image-upload" className="upload-label">
                  <div className="upload-icon">
                    <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                      <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                      <polyline points="17 8 12 3 7 8"></polyline>
                      <line x1="12" y1="3" x2="12" y2="15"></line>
                    </svg>
                  </div>
                  <p>Click to upload an image</p>
                </label>
                <input 
                  type="file" 
                  id="image-upload" 
                  accept="image/*" 
                  onChange={handleImageChange} 
                  style={{ display: 'none' }}
                />
              </div>
              <div className="scan-option">
                <button className="scanner-btn" onClick={toggleScanner}>
                  Scan Barcode/QR Code
                </button>
                <button className="test-scan-btn" onClick={testScan}>
                  Test Scan (Demo)
                </button>
              </div>
            </div>
          ) : scannerActive ? (
            <div className="scanner-container">
              <div id="reader"></div>
              
              {scannerStatus && !scanResult && !scanError && (
                <div className="scanner-status">
                  <p>{scannerStatus}</p>
                </div>
              )}
              
              {scanError && (
                <div className="scan-error">
                  <div className="scan-error-icon">!</div>
                  <p>{scanError}</p>
                  <div className="scan-troubleshooting">
                    <p><strong>Troubleshooting tips:</strong></p>
                    <ul>
                      <li>Ensure your camera is working properly</li>
                      <li>Allow camera permissions in your browser</li>
                      <li>Make sure there is enough light</li>
                      <li>Hold the barcode steady and center it in the frame</li>
                    </ul>
                  </div>
                </div>
              )}
              
              {scanResult && (
                <div className="scan-success">
                  <div className="scan-success-icon">✓</div>
                  <p className="scan-result">{scanResult}</p>
                </div>
              )}
              
              <div className="scanner-controls">
                <button className="clear-btn" onClick={toggleScanner}>
                  {scanResult ? 'Go Back' : 'Cancel Scan'}
                </button>
                {!scanResult && (
                  <button className="test-scan-btn" onClick={testScan}>
                    Test Scan (Demo)
                  </button>
                )}
              </div>
            </div>
          ) : (
            <div className="image-preview-container">
              <img src={imagePreview} alt="Preview" className="image-preview" />
              <div className="preview-actions">
                <button 
                  className="analyze-btn" 
                  onClick={handleAnalyzeImage}
                  disabled={isAnalyzing}
                >
                  {isAnalyzing ? 'Analyzing...' : 'Analyze Ingredients'}
                </button>
                <button className="clear-btn" onClick={handleClearImage}>
                  Clear
                </button>
              </div>
            </div>
          )}
        </div>
        
        {analysisResults && (
          <div className="analysis-results">
            <h3>Analysis Results</h3>
            <p>{analysisResults.message}</p>
          </div>
        )}
      </div>
      
      {/* Text-based Chatbox */}
      <div className="gemini-chatbox ingredients-chat">
        <h3>Ask Questions About Ingredients</h3>
        <p className="powered-by">Powered by Gemini AI</p>
        
        <div className="chat-messages">
          {chatHistory.length === 0 ? (
            <p className="chat-placeholder">Your conversation will appear here. Ask questions about beauty ingredients!</p>
          ) : (
            chatHistory.map((msg, index) => (
              <div key={index} className={`chat-message ${msg.sender}`}>
                <span className="message-text">{msg.text}</span>
              </div>
            ))
          )}
          {isProcessing && (
            <div className="chat-message ai processing">
              <span className="message-text">Gemini is analyzing...</span>
            </div>
          )}
        </div>
        
        <form className="chat-input" onSubmit={handleSendMessage}>
          <input
            type="text"
            placeholder="Ask about ingredients or products..."
            value={chatMessage}
            onChange={(e) => setChatMessage(e.target.value)}
            disabled={isProcessing}
          />
          <button 
            type="submit" 
            className="send-btn"
            disabled={!chatMessage.trim() || isProcessing}
          >
            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <line x1="22" y1="2" x2="11" y2="13"></line>
              <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
            </svg>
          </button>
        </form>
      </div>
    </section>
  );
}
