// src/components/SignIn.js
import { NavLink } from 'react-router-dom'
import { useState } from 'react';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [pw, setPw] = useState('');
  function submit(e) {
    e.preventDefault();
    alert('Sign in successful! (Demo only)');
  }
  return (
    <section className="signin-container">
      <h2>Welcome Back</h2>
      <p>Sign in to access your personalized beauty recommendations</p>
      <form onSubmit={submit}>
        <div className="form-group">
          <label>Email</label>
          <input type="email" className="form-control" value={email}
            onChange={e => setEmail(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input type="password" className="form-control" value={pw}
            onChange={e => setPw(e.target.value)} required />
        </div>
        <button type="submit" className="signin-btn">Sign In</button>
      </form>
      <div className="signup-option">
        <p>Don't have an account?</p>
        <button className="create-account-btn" onClick={() => {
          const email = prompt('Enter email (demo)');
          if (email) alert('Account created! (Demo)');
        }}>Create Account</button>
      </div>
      <div className="skip-option">
        <NavLink to="/">Continue without an account</NavLink>
      </div>
    </section>
  );
}
